CREATE TABLE IF NOT EXISTS books (
    `bookID` INT,
    `title` VARCHAR(209) CHARACTER SET utf8,
    `authors` VARCHAR(540) CHARACTER SET utf8,
    `language_code` VARCHAR(5) CHARACTER SET utf8,
    `publisher` VARCHAR(58) CHARACTER SET utf8,
    `status` VARCHAR(9) CHARACTER SET utf8,
    `quantity` INT,
    `department` VARCHAR(4) CHARACTER SET utf8
);
